import { Component } from '@angular/core';

@Component({
  selector: 'app-rodape-acoes',
  templateUrl: './rodape-acoes.component.html',
  styleUrls: ['./rodape-acoes.component.css']
})
export class RodapeAcoesComponent {
  actions: any[] = [
    { name: 'Ação 1', description: 'Descrição da Ação 1' },
    { name: 'Ação 2', description: 'Descrição da Ação 2' },
    { name: 'Ação 3', description: 'Descrição da Ação 3' }
  ];

  slideConfig = {
    arrows: true,
    dots: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 2000,
    slidesToShow: 1,
    slidesToScroll: 1
  };
}
